  interface Adventure {
    id:number;
    health: number;
    attack:number;
    accuracy:number;
    weapons:number;
    items:number;
    villains:number;
  }
  interface Page {
    page:number;
  }

  const [page, setPage] = useState<Page>(0);

  const [adventure, setAdventure] = useState<Adventure[]> ([])

  const getAdventure = () => {
    axios.get('https://story-back-end.herokuapp.com/api/story')
