import React, { useState } from 'react'
interface Props {
  handleUpdate: (editAdventure:Adventure) => any
  setAdventure: React.Dispatch<React.SetStateAction<Adventure[]>>;
  id:number;
  health: number;

}
interface Adventure {
    id:number;
    health: number;
    attack:number;
    accuracy:number;
    weapons:number;
    items:number;
    villains:number
}
const Edit: React.FC<Props> = (props) => {
    const [adventure, setAdventure] = useState<Adventure[]> ([])
  const [editAdventure, setEditAdventure] = useState<Adventure>({
    id:props.id,
    health: adventure[props.id] ? adventure[props.id].health : 0,
    attack:0,
    accuracy:0,
    weapons:0,
    items:0,
    villains:0
  });
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setEditAdventure({ ...editAdventure, [name]: value });
  };
  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    props.handleUpdate(editAdventure);
  };
  return (
    <form onSubmit={handleSubmit}>
    <label>
        Health:
        <input
          type="number"
          name="health"
          value={editAdventure.health}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Attack:
        <input
          type="number"
          name="attack"
          value={editAdventure.attack}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Accuracy:
        <input
          type="number"
          name="accuracy"
          value={editAdventure.accuracy}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Weapons:
        <input
          type="number"
          name="weapons"
          value={editAdventure.weapons}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Items:
        <input
          type="number"
          name="items"
          value={editAdventure.items}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Villains:
        <input
          type="number"
          name="villains"
          value={editAdventure.villains}
          onChange={handleChange}
        />
      </label>
      <br />
      <button type="submit">Save</button>
    </form>
  );
}
export default Edit;
